import {useContext} from 'react'
import {AppContext} from '../App'
import OneDay from "./OneDay"
import Navbar from './Navbar'
import { addToLocalStorage, getFromLocalStorage } from "../helpers/localStorage";

const Favorite = (props)=> {
    let favorites = getFromLocalStorage('favorites');
    const fetchWeather = (id) => {
        fetch(
            `http://dataservice.accuweather.com/currentconditions/v1/${id}?apikey=lBHawUgG9LRqS3TZNMPItFBx1VH87wIt`
          )
            .then((res) => res.json())
            .then((data) => {
                console.log(data);
                setOneDay(data);
            })
            .catch((err) => {
                console.log(err)
            })
        setOneDay(OneChange)

    }


    const {chosen, setChosen, favKeys} = AppContext;
    console.log(favKeys)

    if(favorites.length < 1) {
        return (
            <>
            <Navbar/>
            <p>no favorites yet</p>
            </>

        )
    } else {
        return (
            <>
            <Navbar/>
            <p>heki=</p>
            {
                favorites.map ((item, i)=>{
                    fetch(
                        `http://dataservice.accuweather.com/currentconditions/v1/${item.key}?apikey=lBHawUgG9LRqS3TZNMPItFBx1VH87wIt`
                      )
                        .then((res) => res.json())
                        .then((data) => {
                            console.log(data);
                            return (
                                <div>
                                    {item.country}, 
                                    {item.city}
                                    <p>{data[0].Temperature.Metric.Value}</p>
                                    <p>{data[0].WeatherIcon}</p>
                                    <p>{data[0].WeatherText}</p> 
                                </div>
                            );
                        })
                        .catch((err) => {
                            console.log(err)
                        })

                })
            }
            </>
        )
    }


}

export default Favorite



