import { useEffect, useState} from 'react'
import OneDay from "./OneDay"
import OneChange from './OneChange'
import Navbar from './Navbar'
import { addToLocalStorage, getFromLocalStorage } from "../helpers/localStorage";
import ErrorBoundary from './ErrorBoundary';
import { useContext } from 'react';
import { AppContext } from '../App';



const Favorite = (props)=> {
    // const [test, setTest] = useState([])

    const [favKeys, setFav] = useState(getFromLocalStorage('favorites')|| [])
    console.log(favKeys)


    // useEffect(()=>{

    //  },[])

        if(favKeys.length < 1) {
            return (
                <>
                <Navbar/>
                <p>no favorites yet</p>
                </>

            )
        } else {

            return (
                <>
                <Navbar/>
                <p>heki=</p>
                {
                    favKeys.map ((item, i) => {

                        // fetch(
                        //     `http://dataservice.accuweather.com/currentconditions/v1/${item.key}?apikey=lBHawUgG9LRqS3TZNMPItFBx1VH87wIt`
                        //   )
                        //     .then((res) => res.json())
                        //     .then((res) => {
                        //         console.log(res);
                                // setTest(res)

                                return (

                                    <div key={i}>
                                            {/* {item.country}, 
                                            {item.city},  */}
                                            {/* {item.res[0].Temperature.Metric.Value},
                                            {item.res[0].WeatherText} */}
                                            <ErrorBoundary> <OneDay keyF={item.key} country ={item.country} city ={item.city} /> </ErrorBoundary>

                                            <button onClick = {()=>{
                                                favKeys.splice(favKeys.indexOf(item), 1);
                                                addToLocalStorage('favorites', favKeys);
                                                setFav(getFromLocalStorage('favorites'))
                                            }}>Remove from favorites</button>
                                    </div>
                                    )

                            })
                    //         .catch(err => 
                    //             console.log(err)
                    //         )

                    // })
                }
                </>
            )
}


}

export default Favorite

