import {useState, createContext} from 'react'
import FiveDaysEx from './components/FiveDaysWeatherEx.json'
import OneDaysEx from './components/OneDayWeatherEx'
import AutocompleteEx from './components/AutoCompleteEx'
import './App.css';
import ErrorBoundary from './components/ErrorBoundary';
import Navbar from './components/Navbar'
import Search from './components/Search'
import OneDay from './components/OneDay';
import FiveDays from './components/FiveDay';
export const AppContext = createContext();

function App() {
  // console.log(FiveDaysEx.DailyForecasts)
  // console.log(OneDaysEx)
  // console.log(AutocompleteEx)
  const [input,setInput]=useState('');
  const [key,setKey]=useState('');
  const [favKeys,setFav]=useState([]);
  console.log(AppContext)
  const [keyList,setKeyList]=useState(AutocompleteEx)
  const [oneDay, setOneDay]=useState(OneDaysEx)
  const [fiveDays, setFiveDay]=useState(FiveDaysEx.DailyForecasts)

  return (
    <AppContext.Provider value={{keyList,setKeyList,input,setInput,key,setKey, oneDay, setOneDay,fiveDays, setFiveDay,favKeys }}>
      <div className="App">
      <Navbar/>
      <ErrorBoundary><Search/></ErrorBoundary>
      <ErrorBoundary><OneDay/></ErrorBoundary>
      <ErrorBoundary><FiveDays/></ErrorBoundary>
    </div>
    </AppContext.Provider>

  );
}

export default App;
