import {useContext} from 'react'
import {AppContext} from '../App'
import OneDay from "./OneDay"
import Navbar from './Navbar'
import { addToLocalStorage, getFromLocalStorage } from "../helpers/localStorage";

const Favorite = (props)=> {
    let favorites = getFromLocalStorage('favorites')


    const {chosen, setChosen} = AppContext
    return (

        <>
        <Navbar/>
        {
            favorites.map((item)=>{
                setChosen({country:item.country, city:item.city, key:item.key})
                return (
                    <div>
                        <p>{chosen.country}, {chosen.city}</p>
                        <button>get forcast</button>

                 </div>
                )
            })
        }

        </>
    )
}

export default Favorite
