import ErrorBoundary from './ErrorBoundary';
import Navbar from './Navbar'
import Search from './Search'
import OneDay from './OneDay';
import FiveDays from './FiveDay';
import { useContext } from 'react';
import { AppContext } from '../App';
import { addToLocalStorage, getFromLocalStorage } from "../helpers/localStorage";


const Home = (props) => {
    const {chosen, oneDay} = useContext(AppContext)

    return (
        <div>
        <Navbar/>
        <ErrorBoundary><Search/></ErrorBoundary>
        <ErrorBoundary><OneDay data={oneDay}/></ErrorBoundary>
        <ErrorBoundary><FiveDays/></ErrorBoundary>

        <button onClick={()=>{
            // console.log(`${window.localStorage} local st`)
            let favorite = [];
            favorite.push(chosen);
            // setFav(favorite);
            addToLocalStorage('favorites', favorite)
            // console.log(`${window.localStorage} local st`)
            // let curFav = getFromLocalStorage('favorites')
            // console.log(curFav)
            // let curFav = getFromLocalStorage('favorites')
            // curFav = curFav.push(chosen)
            //  console.log(favKeys)
            // addToLocalStorage('favorites', curFav)
            // console.log(`fav keys: ${favKeys.key}`);
            }}>Add to Favorite</button>


        </div>


    )

}

export default Home
